(function () {
  'use strict'

  let isDrawing, lastPoint
  const container = document.getElementById('scratchcard')
  const canvas = document.getElementById('goldenFoil')
  const canvasWidth = canvas.width
  const canvasHeight = canvas.height
  const ctx = canvas.getContext('2d')
  const image = new Image()
  const brush = new Image()

  let timeout
  let avyCodeLock = false // API鎖
  const prizeValue = document.querySelector('#prizeValue') // 銀幣的DOM

  document.body.classList.add('loaded')
  document.querySelector('video').pause()
  document.querySelector('video').playbackRate = 1
  document.querySelector('video').play()

  // base64 Workaround because Same-Origin-Policy

  image.src = 'data:image/png;base64,'
  image.onload = function () {
    ctx.drawImage(image, 0, 0)
    // Show the form when Image is loaded.
    // document.querySelectorAll('.form')[0].style.visibility = 'visible';
  }
  brush.src = 'data:image/png;base64,'

  canvas.addEventListener('mousedown', handleMouseDown, false)
  canvas.addEventListener('touchstart', handleMouseDown, false)
  canvas.addEventListener('mousemove', handleMouseMove, false)
  canvas.addEventListener('touchmove', handleMouseMove, false)
  canvas.addEventListener('mouseup', handleMouseUp, false)
  canvas.addEventListener('touchend', handleMouseUp, false)

  const iconSwipe = document.querySelector('.icon-swipe')

  function distanceBetween (point1, point2) {
    return Math.sqrt(Math.pow(point2.x - point1.x, 2) + Math.pow(point2.y - point1.y, 2))
  }

  function angleBetween (point1, point2) {
    return Math.atan2(point2.x - point1.x, point2.y - point1.y)
  }

  // Only test every `stride` pixel. `stride`x faster,
  // but might lead to inaccuracy
  function getFilledInPixels (stride) {
    if (!stride || stride < 1) { stride = 1 }

    const pixels = ctx.getImageData(0, 0, canvasWidth, canvasHeight)
    const pdata = pixels.data
    const l = pdata.length
    const total = (l / stride)
    let count = 0

    // Iterate over all pixels
    for (let i = count = 0; i < l; i += stride) {
      if (parseInt(pdata[i]) === 0) {
        count++
      }
    }
    // console.log('count:' + count)
    return Math.round((count / total) * 100)
  }

  function getMouse (e, canvas) {
    let offsetX = 0; let offsetY = 0; let mx; let my

    if (canvas.offsetParent !== undefined) {
      do {
        offsetX += canvas.offsetLeft
        offsetY += canvas.offsetTop
      } while ((canvas = canvas.offsetParent))
    }

    mx = (e.pageX || e.touches[0].clientX) - offsetX
    my = (e.pageY || e.touches[0].clientY) - offsetY

    return { x: mx, y: my }
  }

  function handlePercentage (filledInPixels) {
    filledInPixels = filledInPixels || 0
    // console.log(filledInPixels + '%')

    if ((filledInPixels < 53 && filledInPixels > 49) && !avyCodeLock) {
      avyCodeLock = true

      if (avyCodeLock) {
        getAvyAction()
      }
    } else if (filledInPixels > 53) {
      clearTimeout(timeout)
      canvas.classList.add('reveal')
      timeout = setTimeout(() => {
        prizeModal.show()
      }, 1500)
    }
  }

  function handleMouseDown (e) {
    isDrawing = true
    lastPoint = getMouse(e, canvas)
  }

  function handleMouseMove (e) {
    if (!isDrawing) { return }

    e.preventDefault()

    iconSwipe.style.display = 'none'

    const currentPoint = getMouse(e, canvas)
    const dist = distanceBetween(lastPoint, currentPoint)
    const angle = angleBetween(lastPoint, currentPoint)
    let x; let y

    for (let i = 0; i < dist; i++) {
      x = lastPoint.x + (Math.sin(angle) * i) - 25
      y = lastPoint.y + (Math.cos(angle) * i) - 25
      ctx.globalCompositeOperation = 'destination-out'
      ctx.drawImage(brush, x, y)
    }

    lastPoint = currentPoint
    handlePercentage(getFilledInPixels(32))
  }

  function handleMouseUp (e) {
    isDrawing = false
  }
})()

async function getAvyAction () { // 取得活動錢幣
  try {
    const { data: { code, data } } = await instance.post(`${BASE_URL}/api/AvyAction/Birthday`, {
      memberId,
      token
    })

    if (code === 200) {
      const { avy_code: avyCode } = data

      const avyCodeMap = {
        10300: 300,
        10200: 200,
        10100: 100
      }

      prizeValue.innerHTML = `$${avyCodeMap[avyCode]}元`

      getAvyCode(avyCode)

      avyCodeLock = false
    } else {
      APIErrorPrompt(code)
    }
  } catch (error) {
    console.log(error)
  }
}
